    def config_barramento(self, Barramento):
        caminho_arq = sys.argv[0]
        caminho_arq = os.path.abspath(caminho_arq)
        caminho_dir = os.path.dirname(caminho_arq)

        mestre_axi = self.textEdit.toPlainText()
        endereco_es = self.textEdit_2.toPlainText()

        config = configparser.ConfigParser()
        config.read(caminho_dir + '/config.ini')

        if self.checkBox.isChecked():
            config['Barramento']['check_axi'] = 'TRUE'
            config['Barramento']['mestre_axi'] = mestre_axi
            config['Barramento']['endereco_es'] = endereco_es
        else:
            config['Barramento']['check_axi'] = 'FALSE'

        if self.checkBox_2.isChecked():
            config['Barramento']['check_reset'] = 'TRUE'
        else:
            config['Barramento']['check_reset'] = 'FALSE'

        with open('config.ini', 'w') as configfile:
            config.write(configfile)
        Barramento.close()

